const Campground=require("../../models/campground"),express=require("express"),router=express.Router(),isCampgroundOwner=require("../../middleware/isCampgroundOwner");router.delete("/:id",isCampgroundOwner,async(e,r,d)=>{try{return(await Campground.findById(e.params.id)).remove(),e.flash("success","Campground succesfully deleted!"),r.redirect("/campgrounds")}catch(e){return e.shouldRedirect=!0,d(e)}}),module.exports=router;