const{campgroundService:campgroundService}=require("../../services/index"),express=require("express"),router=express.Router(),isCampgroundOwner=require("../../middleware/isCampgroundOwner");router.delete("/:id",isCampgroundOwner,async(e,r,d)=>{try{return(await campgroundService.findById(e.params.id)).deleteOne(),e.flash("success","Campground succesfully deleted!"),r.redirect("/campgrounds")}catch(e){return e.shouldRedirect=!0,d(e)}}),module.exports=router;