const{campgroundService:campgroundService}=require("../../services/index"),express=require("express"),router=express.Router();router.get("/:id",async(e,r,s)=>{try{const o=await campgroundService.findById(e.params.id);return await o.populate("comments").execPopulate(),r.render("campgrounds/show-campground",{campground:o,api:process.env.GEOCODER_API_KEY_RESTRICTED})}catch(e){e.messageForConsole=e,e.shouldRedirect=!0,e.statusCode="404",e.message="You got lost. This route does not exists!",s(e)}}),module.exports=router;