const Campground=require("../../models/campground"),express=require("express"),router=express.Router();router.get("/:id",async(e,r,o)=>{try{const s=await Campground.findById(e.params.id).populate("comments");return r.render("campgrounds/show-campground",{campground:s,api:process.env.GEOCODER_API_KEY_RESTRICTED})}catch(e){e.messageForConsole=e,e.shouldRedirect=!0,e.statusCode="404",e.message="You got lost. This route does not exists!",o(e)}}),module.exports=router;