class CommentService{constructor(t){this.Comment=t,this.getAll=this.getAll.bind(this),this.create=this.create.bind(this),this.findById=this.findById.bind(this),this.findByIdAndUpdate=this.findByIdAndUpdate.bind(this)}async create(t){return await this.Comment.create(t)}async getAll(){return await this.Comment.find({})}async findById(t){return await this.Comment.findById(t)}async findByIdAndUpdate(t,i){return await this.Comment.findByIdAndUpdate(t,i)}async findByIdAndRemove(t){return await this.Comment.findByIdAndRemove(t)}}module.exports=CommentService;